(function executeRule(current, previous /*null when async*/) {
/* ================ Using Script Include to Check the past date ================
	// rightnow stores the current time
	var rightnow = new GlideDateTime();

	// Create GlideDateTime obj for the When needed date
	var whenNeeded = new GlideDateTime(current.u_when_needed);

	// If the When Needed date is before rightnow, donot write the record to the database 
	// Output an error message to the screen
	if(whenNeeded.before(rightnow)){
		gs.addInfoMessage("When Needed cannot be in the past. Your request has not been saved to the database");
		current.setAbortAction(true);
	}


	// Challenge Question: Do not allow same-day request
	// Get the data portion of rightnow and whenNeeded(no timestamp)
	var today = rightnow.getLocalDate();
	var isToday = whenNeeded.getLocalDate();

	// Compare today and isToday to see if they are same day
	if(today.compareTo(isToday) == 0){
		gs.addErrorMessage("You cannot submit NeedIt requests for today.");
		current.setAbortAction(true);
	}

*/    	
	// ========= Implementating it using Script Include ============
	// Instantiate the NeedItUtils class. Call the isDatePast method and pass the u_when_needed value.
	var niutils = new NeedItUtils();
	var isPast = niutils.isDatePast(current.u_when_needed);

	// If the isDatePast method return true, the date is in the past.
	if(isPast == true){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

	// ========= Implementating it using Script Include ============
	var isToday = niutils.isDateToday(current.u_when_needed);

	// if the isDateToday method true the When needed date is today
	if(isToday == true){
		gs.addErrorMessage("You cannot submit NeedIt requests for today.");
		current.setAbortAction(true);
	}


})(current, previous);
